import { useState } from "react";
import axios from "axios";
import '../styles/SearchPage.css';

interface SearchProps {
  setSelectedTrack: (track: any) => void;
}

export default function SearchPage({ setSelectedTrack }: SearchProps) {
  const [query, setQuery] = useState("");
  const [tracks, setTracks] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);

  const SPOTIFY_ACCESS_TOKEN = 'BQC0JSQTA01yL9Ogqj345Ls5iJ6uwLcdmHJ-EEZO-UENI9HJho7fZqmYkZIe7TxGTUVX1AO998ZlyfbeFuBVTrigMhwneBBNSRE1OTnaXPGb_anLvQghaDC95TTzvDdRN1elKY3NiOA';

  const searchSongs = async () => {
    if (!query) return;

    setLoading(true);

    try {
      const response = await axios.get('https://api.spotify.com/v1/search', {
        headers: {
          Authorization: `Bearer ${SPOTIFY_ACCESS_TOKEN}`,
        },
        params: {
          q: query,
          type: 'track',
          limit: 10,
        },
      });

      const tracksData = response.data.tracks.items;
      setTracks(tracksData);
    } catch (error) {
      console.error('Error fetching songs from Spotify:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTrackClick = (track: any) => {
    setSelectedTrack(track);
  };

  return (
    <div className="search-container">
      <div className="search-box">
        <input
          type="text"
          placeholder="Search your favorite songs here"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="search-box2"
        />
        <button onClick={searchSongs} className="search-button">
          {loading ? 'Searching...' : 'Search'}
        </button>

        <div>
          {tracks.map((track: any) => (
            <div
              key={track.id}
              className="song-card"
              onClick={() => handleTrackClick(track)}
              style={{ cursor: "pointer" }}
            >
              <img src={track.album.images[0]?.url} alt={track.name} width="50" />
              <p>{track.name} - {track.artists[0]?.name}</p>

              {/* ðŸ”¥ Audio preview if available */}
              {track.preview_url ? (
                <audio controls src={track.preview_url}>
                  Your browser does not support the audio element.
                </audio>
              ) : (
                <p>No preview available</p>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
