import { useEffect, useState } from "react";

declare global {
  interface Window {
    onSpotifyWebPlaybackSDKReady: () => void;
    Spotify: any;
  }
}

interface SongPlayerProps {
  trackUri: string;
  trackImage: string;
  trackName: string;
  trackArtist: string;
}

export default function SongPlayer({ trackUri, trackImage, trackName, trackArtist }: SongPlayerProps) {
  const [player, setPlayer] = useState<any>(null);
  const [deviceId, setDeviceId] = useState<string | null>(null);

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://sdk.scdn.co/spotify-player.js";
    script.async = true;
    document.body.appendChild(script);

    window.onSpotifyWebPlaybackSDKReady = () => {
      const token = "BQAVukd_vAV5OJSf6hdNgSY9PAIOFXAFuz51lro04ZivPTDmzYc9wWTJ-R281WKK9uSHOJwbP8aYUK4WWQZHyV2BbC3gLoxVPZrsDQ9XPhHZ5-BomiKaq8okjtSVrqERGSdEcuoVBqI"; // Ideally get this from your backend
      const player = new window.Spotify.Player({
        name: "My Web Player",
        getOAuthToken: (cb: any) => { cb(token); },
        volume: 0.5,
      });

      setPlayer(player);

      player.addListener('ready', ({ device_id }: any) => {
        console.log('Ready with Device ID', device_id);
        setDeviceId(device_id);
      });

      player.addListener('initialization_error', ({ message }: any) => {
        console.error(message);
      });
      player.addListener('authentication_error', ({ message }: any) => {
        console.error(message);
      });
      player.addListener('account_error', ({ message }: any) => {
        console.error(message);
      });
      player.addListener('playback_error', ({ message }: any) => {
        console.error(message);
      });

      player.connect();
    };
  }, []);

  useEffect(() => {
    const play = async () => {
      if (!deviceId || !trackUri) return;

      await fetch(`https://api.spotify.com/v1/me/player/play?device_id=${deviceId}`, {
        method: "PUT",
        body: JSON.stringify({ uris: [trackUri] }),
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer BQAVukd_vAV5OJSf6hdNgSY9PAIOFXAFuz51lro04ZivPTDmzYc9wWTJ-R281WKK9uSHOJwbP8aYUK4WWQZHyV2BbC3gLoxVPZrsDQ9XPhHZ5-BomiKaq8okjtSVrqERGSdEcuoVBqI`, // Same token here
        },
      });
    };

    play();
  }, [deviceId, trackUri]);

  return (
    <div className="p-4 bg-gray-700 mt-6 rounded">
      <img src={trackImage} alt={trackName} width="100" className="mb-2" />
      <p className="text-white">{trackName} - {trackArtist}</p>
    </div>
  );
}
